	function ReverseString(&$s, $from, $to)
	{
		while ($from < $to) {
			$t = $s[$from];
			$s[$from++] = $s[$to];
			$s[$to--] = $t;
		}
	}

	function LeftRotateString(&$s,$n,$m)
	{
		$m %= $n;               
		ReverseString($s, 0, $m - 1);
		// echo $s; //bacdef
		ReverseString($s, $m, $n - 1);
		// echo $s; //bafedc
		ReverseString($s, 0, $n - 1);
		// echo $s; //cdefab
	}

	$String = 'abcdef';
	$n = 6;
	$m = 2;
	LeftRotateString($String,$n,$m);
	// echo $String; //cdefab

