~~~ PHP

/**
 * 
 */
abstract class PersonAbstract
{
    /**
     * 
     * @var string
     */
    protected $_gender = '';

    /**
     * 
     * @var string
     */
    protected $_tool   = '';

    /**
     * 
     *
     * @param string       $gender 
     * @param EatInterface $tool   [description]
     */
    public function __construct($gender='',EatInterface $tool)
    {
        $this->_gender = $gender;
        $this->_tool   = $tool;
    }

    /**
     * 
     *
     * @param  string $food 
     * @return void
     */
    abstract public function eat($food='');
}
/**
 * 
 */
class PersonMale extends PersonAbstract
{
    /**
     * 
     *
     * @param  string $food 
     * @return string
     */
    public function eat($food='')
    {
        $this->_tool->eat($food);
    }
}
/**
 * 
 */
interface EatInterface
{
    /**
     * 
     *
     * @param  string $food 
     * @return mixed
     */
    public function eat($food='');
}

/**
 * 
 */
class EatByFork implements EatInterface
{
    /**
     * 
     *
     * @param  string $food 
     * @return string
     */
    public function eat($food='')
    {
        echo "{$food}~";
    }
}

/**
 * 
 */
class EatByChopsticks implements EatInterface
{
    /**
     * 
     *
     * @param  string $food 
     * @return string
     */
    public function eat($food='')
    {
        echo "{$food}~";
    }
}

try {
    // 
    $male = new PersonMale('male', new EatByChopsticks());
    // 
    $male->eat('');

} catch (Exception $e) {
    echo $e->getMessage();
}


~~~

