$observable = new Observable();
// 
$observerExampleOne = new ObserverExampleOne();
$observerExampleTwo = new ObserverExampleTwo();

// 
$observable->attach($observerExampleOne);
$observable->attach($observerExampleTwo);

// 
$observable->notify();

