class SqlInterpreter
{
    /**
     * 
     * @var string
     */
    private $_tableName = '';

    /**
     * 
     * @var object
     */
    private static $_instance;

    /**
     * 
     *
     * @param string $table 
     */
    public static function db($tableName='')
    {
        if (empty($tableName)) {
            throw new Exception("argument tableName is null", 400);
        }
        // 
        if(!self::$_instance instanceof self){// instanceof
            self::$_instance = new self();
        }
        // 
        self::$_instance->_setTableName($tableName);
        // 
        return self::$_instance;
    }

    /**
     * 
     *
     * @param string $tableName 
     */
    private function _setTableName($tableName='')
    {
        $this->_tableName = $tableName;
    }

    /**
     *  
     *
     * @param  array $data 
     * @return mixed
     */
    public function insert($data=[])
    {
        if (empty($data)) {
            throw new Exception("argument data is null", 400);
        }
        $count = count($data);
        //
        $field = array_keys($data);
        $fieldString = '';
        foreach ($field as $k => $v) {
            if ($k === (int)($count - 1)) {
                $fieldString .= "`{$v}`";
                continue;
            }
            $fieldString .= "`{$v}`".',';
        }
        unset($k);
        unset($v);

        //
        $value = array_values($data);
        $valueString = '';
        foreach ($value as $k => $v) {
            if ($k === (int)($count - 1)) {
                $valueString .= "'{$v}'";
                continue;
            }
            $valueString .= "'{$v}'".',';
        }
        unset($k);
        unset($v);

        $sql = "INSERT INTO `{$this->_tableName}` ({$fieldString}) VALUES ({$valueString})";

        echo $sql . "\n";
    }

    /**
     *  
     *
     * @param  array $data 
     * @return mixed
     */
    public function delete($data=[])
    {
        if (empty($data)) {
            throw new Exception("argument data is null", 400);
        }
        // where
        $count = (int)count($data);
        $where = '';
        $dataCopy = $data;
        $pop = array_pop($dataCopy);
        if ($count === 1) {
            $field = array_keys($data)[0];
            $value = array_values($data)[0];
            $where = "`{$field}` = '{$value}'";
        }else{
            foreach ($data as $k => $v) {
                if ($v === $pop) {
                    $where .= "`{$k}` = '{$v}'";
                    continue;
                }
                $where .= "`{$k}` = '{$v}' AND ";
            }
        }

        $sql = "DELETE FROM `{$this->_tableName}` WHERE {$where}";

        echo $sql . "\n";
    }

    /**
     *  
     *
     * @param  array $data 
     * @return mixed
     */
    public function update($data=[])
    {
        if (empty($data)) {
            throw new Exception("argument data is null", 400);
        }
        if (empty($data['id'])) {
            throw new Exception("argument data['id'] is null", 400);
        }
        $set = '';
        $dataCopy = $data;
        $pop = array_pop($dataCopy);
        foreach ($data as $k => $v) {
            if ($v === $pop) {
                $set .= "`{$k}` = '$v'";
                continue;
            }
            $set .= "`{$k}` = '$v',";
        }

        $sql = "UPDATE `{$this->_tableName}` SET {$set}";

        echo $sql . "\n";
    }

    /**
     *  
     *
     * @param  array $data 
     * @return mixed
     */
    public function find($data=[])
    {
        if (empty($data)) {
            throw new Exception("argument data is null", 400);
        }

        // where
        $count = (int)count($data);
        $where = '';
        $dataCopy = $data;
        $pop = array_pop($dataCopy);
        if ($count === 1) {
            $field = array_keys($data)[0];
            $value = array_values($data)[0];
            $where = "`{$field}` = '{$value}'";
        }else{
            foreach ($data as $k => $v) {
                if ($v === $pop) {
                    $where .= "`{$k}` = '{$v}'";
                    continue;
                }
                $where .= "`{$k}` = '{$v}' AND ";
            }
        }

        $sql = "SELECT * FROM `{$this->_tableName}` WHERE {$where}";

        echo $sql . "\n";
    }
}


