namespace iterator;

/**
 * 
 */
interface School
{
    /**
     * 
     *
     * @return mixed
     */
    public function getIterator();
}

/**
 * 
 */
interface Iterator
{
    /**
     * 
     *
     * @return boolean
     */
    public function hasNext();

    /**
     * 
     *
     * @return object
     */
    public function next();

    /**
     * 
     *
     * @return mixed
     */
    public function current();

    /**
     * 
     *
     * @return mixed
     */
    public function index();
}

/**
 * 
 */
class SchoolExperimental implements School
{
    /**
     * 
     * @var
     */
    private $_teachers = [];

    /**
     * 
     *
     * @param  string $name 
     * @return mixed
     */
    public function __get($name='')
    {
        $name = '_' . $name;
        return $this->$name;
    }

    /**
     * 
     * @param string $name
     */
    public function addTeacher($name='')
    {
        $this->_teachers[] = $name;
    }

    /**
     * 
     *
     * @return mixed
     */
    public function getIterator()
    {
        return new TeacherIterator($this);
    }
}

/**
 * 
 */
class TeacherIterator implements Iterator
{

    /**
     * 
     * @var integer
     */
    private $_index = 0;

    /**
     * 
     * @var object
     */
    private $_teachers;

    /**
     * 
     *
     * @param School $school
     */
    public function __construct(School $school)
    {
        $this->_teachers = $school->teachers;
    }

    /**
     * 
     *
     * @return boolean
     */
    public function hasNext()
    {
        if ($this->_index < count($this->_teachers)) {
            return true;
        }
        return false;
    }

    /**
     * 
     *
     * @return object
     */
    public function next()
    {
        if (!$this->hasNext()) {
            echo NULL;
            return;
        }
        $index = $this->_index + 1;
        echo $this->_teachers[$index];
    }

    /**
     * 
     *
     * @return mixed
     */
    public function current()
    {
        if (!isset($this->_teachers[$this->_index])) {
            echo  NULL;
            return;
        }
        $current = $this->_teachers[$this->_index];
        $this->_index += 1;
        echo $current . "\n";
    }

    /**
     * 
     *
     * @return integer
     */
    public function index()
    {
        echo $this->_index;
    }
}

